const e=[document.querySelector("#about"),document.querySelector("#skills"),document.querySelector("#projects"),document.querySelector("#contact")];window.addEventListener("scroll",(()=>{const t=window.pageYOffset;e.forEach((e=>{const o=e.offsetHeight,c=e.offsetTop-100,r=e.getAttribute("id");t>c&&t<=c+o?document.querySelector('li a[href*="'+r+'"]').classList.add("active"):document.querySelector('li a[href*="'+r+'"]').classList.remove("active")}))}));